mapper.select:
select id, bno, category, title, writer, content, view_cnt, comment_cnt, hit_cnt, reg_date, updated_date
from board
where category=#{category} and bno = #{bno}

mapper.update:
update board
set title = #{title}, content = #{content}, updated_date = sysdate
where category = #{category} and bno = #{bno}
